import './Donarstatus.css'; // Import the CSS file for styles

import React, { useState } from 'react';

export default function Donarstatus() {
  const [foodType, setFoodType] = useState(''); // 'veg', 'non-veg', 'vegan', 'seafood'
  const [amount, setAmount] = useState('');
  const [restaurantName, setRestaurantName] = useState('');
  const [restaurantMobile, setRestaurantMobile] = useState('');
  const [restaurantAddress,setRestaurantAddress] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle the form submission logic here
    console.log('Submitted:', { foodType, amount, restaurantName, restaurantMobile,restaurantAddress });
    // You can add further logic for submitting the data to a server or processing it as needed
  };

  return (
    <div className="donar-status-container">
      <h1>Hello Donar</h1>

      <form onSubmit={handleSubmit} className="submission-form">
        <label>
          Type of Food:
          <select value={foodType} onChange={(e) => setFoodType(e.target.value)}>
            <option value="" disabled>Select</option>
            <option value="veg">Veg</option>
            <option value="non-veg">Non-Veg</option>
            <option value="vegan">Vegan</option>
            <option value="seafood">Seafood</option>
          </select>
        </label>

        <label>
          Amount (kg):
          <input type="number" value={amount} onChange={(e) => setAmount(e.target.value)} />
        </label>

        <label>
          Restaurant Name:
          <input type="text" value={restaurantName} onChange={(e) => setRestaurantName(e.target.value)} />
        </label>

        <label>
          Restaurant Mobile No:
          <input type="tel" value={restaurantMobile} onChange={(e) => setRestaurantMobile(e.target.value)} />
        </label>
        <label>
          Restaurant Address:
          <input type="text" value={restaurantAddress} onChange={(e) => setRestaurantAddress(e.target.value)} />
        </label>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
