import React, { useState } from 'react';
import { Link, Navigate, useNavigate } from 'react-router-dom';
import './Login.css';
import { useForm } from 'react-hook-form';
import { compareSync } from 'bcryptjs';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  let navigate=useNavigate();

  let {register,handleSubmit,formState:{errors}}=useForm();
  function handlleFormSubmit(userCredentials){
    fetch(`http://localhost:4000/donar?username=${userCredentials.username}`)
    .then(res =>res.json())
    .then(userObj=>
      {
        if(userObj.length==0)
          alert("Invalid User")
        else
        {
          if(compareSync(userCredentials.password,userObj[0].password))
            navigate('/donarstatus')
          else
            alert('Invalid Password')
        }
      }
      )
  }

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Login</h2>
        <form onSubmit={handleSubmit(handlleFormSubmit)} className="login-form">
          <div className="input-group">
            <label htmlFor="username">Username:</label>
            <input
              type="text"
              id="username"
              {...register('username',{required:true})}
            />
          </div>

          <div className="input-group">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              {...register('password',{required:true})}
            />
          </div>

          <Link to="/donar/Login/donarstatus">Login</Link>
          {/* type="submit" */}
        </form>
        <p>Don't have an account? <Link to="/donar/register">Register</Link></p>
      </div>
    </div>
  );
};

export default Login;
