import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Login1.css';

const Login1 = () => {
  let navigate=useNavigate();

  let {register,handleSubmit,formState:{errors}}=useForm();
  function handleFormSubmit(userCredentials){
    fetch(`http://localhost:4000/ngo?userid=${userCredentials.userid}`)
    .then(res =>res.json())
    .then(userObj=>
      {
        if(userObj.length==0)
          alert("Invalid User")
        else
        {
          if(compareSync(userCredentials.password,userObj[0].password))
            navigate('/donar/Login/donarstatus')
          else
            alert('Invalid Password')
        }
      }
      )
  }

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Login</h2>
        <form onSubmit={handleLogin} className="login-form">
          <div className="input-group">
            <label htmlFor="username">Username:</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>

          <div className="input-group">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          <Link to="/donar/Login1/ngostatus">Login</Link>
        </form>
        <p>Don't have an account? <Link to="/ngo/register1">Register</Link></p>
      </div>
    </div>
  );
};

export default Login1;
